mapper.update:
UPDATE STUDENT
SET STUDENT_PWD = #{studentPwd}
WHERE STUDENT_NO = #{studentNo} 
AND STATUS = '재학'

UPDATE PROFESSOR
SET PROFESSOR_PWD = #{professorPwd}
WHERE PROFESSOR_NO = #{professorNo} 
AND STATUS = 'Y'

mapper.select:
  		SELECT COUNT(CLASS_NOS) COUNT
		FROM CLASS C
		JOIN BUCKET B ON (C.CLASS_NO = B.CLASS_NO)
		WHERE B.CLASS_NO = #{classNo}
		AND STUDENT_NO = #{studentNo}
		AND STATUS = 'Y'

mapper.delete:
DELETE FROM BUCKET
WHERE CLASS_NO = #{classNo}
AND STUDENT_NO = #{studentNo}

mapper.select:
SELECT DAY,PERIOD,CLASS_HOUR
FROM CLASS
WHERE CLASS_NO = #{classNo}

  		SELECT COUNT(*) COUNT
  		FROM SCHEDULE
		WHERE DAY = #{day}
  		
  			
  				AND (PERIOD = #{period}+1 OR PERIOD = #{period})
  				AND STUDENT_NO = #{studentNo}
  			
  			
  				AND PERIOD = #{period}
  				AND STUDENT_NO = #{studentNo}

SELECT DEPARTMENT_NO
FROM DEPARTMENT
WHERE DEPARTMENT_NAME = #{departmentNo}

SELECT *
FROM COUNSELING
WHERE COUNSEL_NO = #{counselNo}

mapper.update:
UPDATE COUNSELING
SET COUNSEL_CONTENT = #{counselContent}
WHERE COUNSEL_NO = #{counselNo}
AND STATUS = 'N'

UPDATE STUDENT 
SET PHONE = #{phone},
		EMAIL = #{email},
		POST = #{post},
		ADDRESS =#{address}
