AND PHONE = #{phone}
AND STATUS = 'Y'

mapper.update:
UPDATE STUDENT
SET STUDENT_PWD = #{studentPwd}
WHERE STUDENT_NO = #{studentNo} 
AND STATUS = '재학'

UPDATE PROFESSOR
SET PROFESSOR_PWD = #{professorPwd}
WHERE PROFESSOR_NO = #{professorNo} 
AND STATUS = 'Y'
